import React, { useState } from "react";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import AddListModal from "./AddListModal";

const inputStyle = {
  width: "100%",
  padding: "8px",
  marginBottom: "10px",
};

const buttonStyle = {
  padding: "8px 12px",
  backgroundColor: "blue",
  color: "white",
  border: "none",
  cursor: "pointer",
  marginRight: "10px",
};

const listStyle = {
  listStyleType: "none",
  padding: "0",
};

const linkStyle = {
  display: "block",
  fontSize: "16px",
  color: "blue",
  textDecoration: "none",
  marginTop: "10px",
};

const deleteButtonStyle = {
  marginLeft: "10px",
  padding: "5px 8px",
  backgroundColor: "red",
  color: "white",
  border: "none",
  cursor: "pointer",
};

const containerStyle = {
  textAlign: "center",
  fontFamily: "Arial, sans-serif",
};

const ShoppingList = () => {
  const [lists, setLists] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [removeIndex, setRemoveIndex] = useState(null);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleAddList = (newListName) => {
    const newList = { name: newListName, items: [] };
    setLists([...lists, newList]);
    setIsModalOpen(false);
  };

  const confirmDelete = (index) => {
    setRemoveIndex(index);
    openModal();
  };

  const removeList = () => {
    if (removeIndex !== null) {
      const updatedLists = lists.filter((_, index) => index !== removeIndex);
      setLists(updatedLists);
      setRemoveIndex(null);
      closeModal();
    }
  };

  return (
    <div style={containerStyle}>
      <h1>Shopping Lists</h1>
      <button onClick={openModal} style={buttonStyle}>
        Add New List
      </button>
      <ul style={listStyle}>
        {lists.map((list, index) => (
          <li key={index}>
            {list.name}
            <button
              onClick={() => confirmDelete(index)}
              style={deleteButtonStyle}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      <Link to="/" style={linkStyle}>
        Go to Homepage
      </Link>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Confirm Delete Modal"
      >
        <div style={containerStyle}>
          <h2>Confirm Delete?</h2>
          <button onClick={removeList} style={buttonStyle}>
            Yes
          </button>
          <button onClick={closeModal} style={buttonStyle}>
            No
          </button>
        </div>
      </Modal>
      <AddListModal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        onAddList={handleAddList}
      />
    </div>
  );
};

export default ShoppingList;
