import React, { useState } from "react";

function App() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [listName, setListName] = useState("");
  const [savedLists, setSavedLists] = useState([]);
  const [activeList, setActiveList] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editedListName, setEditedListName] = useState("");

  const addItem = () => {
    if (newItem.trim() !== "") {
      setItems([...items, { name: newItem, done: false }]);
      setNewItem("");
    }
  };

  const saveList = () => {
    if (listName.trim() !== "" && items.length > 0) {
      const newList = { name: listName, items: [...items] };
      setSavedLists([...savedLists, newList]);
      setListName("");
      setItems([]);
      setNewItem("");
    }
  };

  const handleInputChange = (event) => {
    setNewItem(event.target.value);
  };

  const handleListNameChange = (event) => {
    setListName(event.target.value);
  };

  const handleListClick = (index) => {
    setActiveList(activeList === index ? null : index);
  };

  const handleEditClick = (list) => {
    setEditMode(true);
    setEditedListName(list.name);
  };

  const handleEditSave = (index) => {
    if (editedListName.trim() !== "") {
      const updatedLists = [...savedLists];
      updatedLists[index].name = editedListName;
      setSavedLists(updatedLists);
      setEditMode(false);
      setEditedListName("");
    }
  };

  const handleItemCheck = (listIndex, itemIndex) => {
    const updatedLists = [...savedLists];
    updatedLists[listIndex].items[itemIndex].done =
      !updatedLists[listIndex].items[itemIndex].done;
    setSavedLists(updatedLists);
  };

  const handleAddToList = (listIndex) => {
    const updatedLists = [...savedLists];
    updatedLists[listIndex].items = [
      ...updatedLists[listIndex].items,
      { name: newItem, done: false },
    ];
    setSavedLists(updatedLists);
    setNewItem("");
  };

  const handleRemoveFromList = (listIndex, itemIndex) => {
    const updatedLists = [...savedLists];
    updatedLists[listIndex].items.splice(itemIndex, 1);
    setSavedLists(updatedLists);
  };

  return (
    <div className="App">
      <h1>Shopping List</h1>
      <div>
        <input
          type="text"
          placeholder="List Name..."
          value={listName}
          onChange={handleListNameChange}
        />
        <input
          type="text"
          placeholder="Add item..."
          value={newItem}
          onChange={handleInputChange}
          onKeyPress={(event) => {
            if (event.key === "Enter") addItem();
          }}
        />
        <button onClick={addItem}>Add</button>
        <button onClick={saveList}>Save List</button>
      </div>
      <ul>
        {items.length > 0 && (
          <li>
            <strong>Current List</strong>
            <ul>
              {items.map((item, i) => (
                <li key={i}>{item.name}</li>
              ))}
            </ul>
          </li>
        )}
        {savedLists.map((list, listIndex) => (
          <li key={listIndex}>
            <div onClick={() => handleListClick(listIndex)}>
              {editMode && activeList === listIndex ? (
                <div>
                  <input
                    type="text"
                    value={editedListName}
                    onChange={(e) => setEditedListName(e.target.value)}
                  />
                  <button onClick={() => handleEditSave(listIndex)}>
                    Save
                  </button>
                </div>
              ) : (
                <div>
                  <strong>{list.name}</strong>
                  <button onClick={() => handleEditClick(list)}>Edit</button>
                </div>
              )}
            </div>
            {activeList === listIndex && (
              <div>
                <ul>
                  {list.items.map((item, itemIndex) => (
                    <li key={itemIndex}>
                      <input
                        type="checkbox"
                        checked={item.done}
                        onChange={() => handleItemCheck(listIndex, itemIndex)}
                      />
                      <span
                        style={{
                          textDecoration: item.done ? "line-through" : "none",
                        }}
                      >
                        {item.name}
                      </span>
                      <button
                        onClick={() =>
                          handleRemoveFromList(listIndex, itemIndex)
                        }
                      >
                        Remove
                      </button>
                    </li>
                  ))}
                </ul>
                <input
                  type="text"
                  placeholder="Add item..."
                  value={newItem}
                  onChange={handleInputChange}
                  onKeyPress={(event) => {
                    if (event.key === "Enter") handleAddToList(listIndex);
                  }}
                />
                <button onClick={() => handleAddToList(listIndex)}>
                  Add to List
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
