import React from "react";
import LanguageToggle from "./LanguageToggle";
import DarkModeToggle from "./DarkModeToggle";

const ShoppingListApp = () => {
  const [items, setItems] = React.useState([]);
  const [newItem, setNewItem] = React.useState("");
  const [darkMode, setDarkMode] = React.useState(false);
  const [language, setLanguage] = React.useState("en");

  const handleInputChange = (event) => {
    setNewItem(event.target.value);
  };

  const addItem = () => {
    if (newItem.trim() !== "") {
      setItems([...items, newItem]);
      setNewItem("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };

  const clearItems = () => {
    setItems([]);
  };

  const sortItems = () => {
    const sortedItems = [...items].sort();
    setItems(sortedItems);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const toggleLanguage = () => {
    setLanguage(language === "en" ? "cz" : "en");
  };

  const containerStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    minHeight: "100vh",
    fontFamily: "Arial, sans-serif",
    backgroundColor: darkMode ? "#333" : "#fff",
    color: darkMode ? "#fff" : "#333",
  };

  const inputStyle = {
    fontSize: "20px",
    padding: "10px",
    marginBottom: "10px",
    width: "70%",
  };

  const buttonStyle = {
    fontSize: "18px",
    padding: "10px 20px",
    margin: "5px",
    backgroundColor: "#4caf50",
    color: "#ffffff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  };

  const listStyle = {
    listStyleType: "none",
    padding: "0",
    textAlign: "left",
    width: "70%",
  };

  const listItemStyle = {
    display: "flex",
    alignItems: "center",
    marginBottom: "10px",
    fontSize: "18px",
  };

  const removeButtonStyle = {
    marginLeft: "10px",
    padding: "6px 12px",
    backgroundColor: "#f44336",
    color: "#ffffff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  };

  return (
    <div style={containerStyle}>
      <DarkModeToggle toggleDarkMode={toggleDarkMode} />
      <LanguageToggle toggleLanguage={toggleLanguage} />
      <h1>Shopping List</h1>
      <input
        type="text"
        value={newItem}
        onChange={handleInputChange}
        placeholder="Add an item..."
        style={inputStyle}
      />
      <button onClick={addItem} style={buttonStyle}>
        Add
      </button>
      <button onClick={clearItems} style={buttonStyle}>
        Clear All
      </button>
      <button onClick={sortItems} style={buttonStyle}>
        Sort
      </button>
      <ul style={listStyle}>
        {items.map((item, index) => (
          <li key={index} style={listItemStyle}>
            {item}
            <button onClick={() => removeItem(index)} style={removeButtonStyle}>
              Remove
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShoppingListApp;
