import React from "react";

const ShoppingListApp = () => {
  const [items, setItems] = React.useState([]);
  const [newItem, setNewItem] = React.useState("");

  const handleInputChange = (event) => {
    setNewItem(event.target.value);
  };

  const addItem = () => {
    if (newItem.trim() !== "") {
      setItems([...items, newItem]);
      setNewItem("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };

  const clearItems = () => {
    setItems([]);
  };

  const sortItems = () => {
    const sortedItems = [...items].sort();
    setItems(sortedItems);
  };

  const buttonStyle = {
    fontSize: "16px",
    padding: "8px 12px",
    margin: "5px",
    backgroundColor: "#4caf50",
    color: "#ffffff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  };

  const listStyle = {
    listStyleType: "none",
    padding: "0",
  };

  const listItemStyle = {
    display: "flex",
    alignItems: "center",
    marginBottom: "5px",
  };

  const removeButtonStyle = {
    marginLeft: "10px",
    padding: "4px 8px",
    backgroundColor: "#f44336",
    color: "#ffffff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  };

  return (
    <div>
      <h1 style={{ fontFamily: "Arial", textAlign: "center" }}>
        Shopping List
      </h1>
      <input
        type="text"
        value={newItem}
        onChange={handleInputChange}
        placeholder="Add an item..."
        style={{ fontSize: "16px", padding: "8px", marginBottom: "10px" }}
      />
      <button onClick={addItem} style={buttonStyle}>
        Add
      </button>
      <button onClick={clearItems} style={buttonStyle}>
        Clear All
      </button>
      <button onClick={sortItems} style={buttonStyle}>
        Sort
      </button>
      <ul style={listStyle}>
        {items.map((item, index) => (
          <li key={index} style={listItemStyle}>
            {item}
            <button onClick={() => removeItem(index)} style={removeButtonStyle}>
              Remove
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShoppingListApp;
