import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from "./components/Home";
import ShoppingList from "./components/ShoppingList";
import ItemList from "./components/ItemList";
import { ShoppingProvider } from "./components/ShoppingContext"; // Import your ShoppingContext
import { ThemeProvider } from "./components/ThemeContext"; // Import the ThemeProvider
import ThemeToggle from "./components/ThemeToggle"; // Import the ThemeToggle

function App() {
  return (
    <Router>
      <ShoppingProvider>
        <ThemeProvider>
          {" "}
          {/* Wrap your app with ThemeProvider */}
          <div className="App">
            <ThemeToggle /> {/* Add ThemeToggle component */}
            <Switch>
              <Route exact path="/" component={Home} />
              <Route exact path="/shopping-list" component={ShoppingList} />
              <Route path="/shopping-list/:listId" component={ItemList} />
            </Switch>
          </div>
        </ThemeProvider>
      </ShoppingProvider>
    </Router>
  );
}

export default App;
