import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";

function ItemList() {
  const { listIndex } = useParams();
  const [lists, setLists] = useState([]);
  const [newItem, setNewItem] = useState("");
  const history = useHistory();

  useEffect(() => {
    // Simulate fetching data for the list (using index as an identifier)
    const fetchedList = localStorage.getItem(`shoppingList_${listIndex}`);
    if (fetchedList) {
      setLists(JSON.parse(fetchedList));
    }
  }, [listIndex]);

  const addItem = () => {
    if (newItem.trim() !== "") {
      const updatedLists = [...lists];
      updatedLists[listIndex].items.push(newItem);
      setLists(updatedLists);
      setNewItem("");
      localStorage.setItem(
        `shoppingList_${listIndex}`,
        JSON.stringify(updatedLists)
      );
    }
  };

  const handleInputChange = (e) => {
    setNewItem(e.target.value);
  };

  const goBackToList = () => {
    history.push("/shopping-list");
  };

  return (
    <div>
      <h1>Add Items to List</h1>
      <h2>List: {lists[listIndex]?.name}</h2>
      <input
        type="text"
        value={newItem}
        onChange={handleInputChange}
        placeholder="Add an item"
      />
      <button onClick={addItem}>Add</button>
      <button onClick={goBackToList}>Back to Lists</button>
      <ul>
        {lists[listIndex]?.items.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
}

export default ItemList;
