import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next"; // Import useTranslation hook
import { ThemeContext } from "./ThemeContext";

const linkStyle = {
  display: "block",
  fontSize: "18px",
  marginTop: "20px",
  textDecoration: "none",
  color: "blue",
};

const containerStyle = {
  textAlign: "center",
  padding: "20px",
  fontFamily: "Arial, sans-serif",
};

const Home = () => {
  const { isDarkMode, toggleTheme } = useContext(ThemeContext);
  const { t, i18n } = useTranslation(); // Use useTranslation hook

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  const toggleButtonStyle = {
    padding: "8px 16px",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    backgroundColor: isDarkMode ? "#333" : "#fff",
    color: isDarkMode ? "#fff" : "#333",
  };

  return (
    <div
      style={{
        ...containerStyle,
        backgroundColor: isDarkMode ? "#333" : "#fff",
        color: isDarkMode ? "#fff" : "#333",
      }}
    >
      <h1>{t("Welcome to my Shopping List App")}</h1>
      <button onClick={() => changeLanguage("en")}>English</button>
      <button onClick={() => changeLanguage("cz")}>Czech</button>
      <button style={toggleButtonStyle} onClick={toggleTheme}>
        {isDarkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
      </button>
      <Link to="/shopping-list" style={linkStyle}>
        {t("Go to Shopping Lists")}
      </Link>
    </div>
  );
};

export default Home;
