import React, { useState } from "react";

const Home = () => {
  const [showSavedLists, setShowSavedLists] = useState(false);
  const [savedLists, setSavedLists] = useState([
    { name: "List 1", items: ["Item 1", "Item 2"] },
    { name: "List 2", items: ["Item 3", "Item 4"] },
    // You can have your saved lists state here
  ]);
  const [selectedListIndex, setSelectedListIndex] = useState(null);

  const showListItems = (index) => {
    setSelectedListIndex(index === selectedListIndex ? null : index);
  };

  const buttonStyle = {
    textDecoration: "none",
    color: "#fff",
    backgroundColor: "#007bff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    margin: "10px",
    display: "inline-block",
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Home Page</h1>
      <button
        onClick={() => setShowSavedLists(!showSavedLists)}
        style={buttonStyle}
      >
        {showSavedLists ? "Hide Saved Lists" : "Show Saved Lists"}
      </button>
      {showSavedLists && (
        <div>
          <h3>Saved Lists:</h3>
          <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
            {savedLists.map((list, index) => (
              <li
                key={index}
                onClick={() => showListItems(index)}
                style={{ cursor: "pointer" }}
              >
                {list.name}
                {selectedListIndex === index && (
                  <ul>
                    {list.items.map((item, idx) => (
                      <li key={idx}>{item}</li>
                    ))}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Home;
