import React, { useState } from "react";

const CreateShoppingList = () => {
  const [listName, setListName] = useState("");
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [isListNamed, setIsListNamed] = useState(false);
  const [savedLists, setSavedLists] = useState([]);

  const handleListNameChange = (event) => {
    setListName(event.target.value);
  };

  const handleListNameSubmit = () => {
    if (listName.trim() !== "") {
      setIsListNamed(true);
    }
  };

  const handleInputChange = (event) => {
    setNewItem(event.target.value);
  };

  const addItem = () => {
    if (newItem.trim() !== "") {
      setItems([...items, newItem]);
      setNewItem("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };

  const clearItems = () => {
    setItems([]);
  };

  const saveList = () => {
    if (isListNamed && items.length > 0) {
      const shoppingList = {
        name: listName,
        items: [...items],
      };
      setSavedLists([...savedLists, shoppingList]);
      setIsListNamed(false);
      setListName("");
      setItems([]);
    }
  };

  const buttonStyle = {
    textDecoration: "none",
    color: "#fff",
    backgroundColor: "#007bff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    margin: "10px",
    display: "inline-block",
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Create Shopping List</h1>
      {!isListNamed ? (
        <div>
          <label>
            Enter List Name:
            <input
              type="text"
              value={listName}
              onChange={handleListNameChange}
              placeholder="Enter list name..."
              style={{ padding: "8px", margin: "10px" }}
            />
          </label>
          <br />
          <button onClick={handleListNameSubmit} style={buttonStyle}>
            Confirm List Name
          </button>
        </div>
      ) : (
        <div>
          <h2>List: {listName}</h2>
          <input
            type="text"
            value={newItem}
            onChange={handleInputChange}
            placeholder="Add an item..."
            style={{ padding: "8px", margin: "10px" }}
          />
          <button onClick={addItem} style={buttonStyle}>
            Add
          </button>
          <button onClick={clearItems} style={buttonStyle}>
            Clear All
          </button>
          <button onClick={saveList} style={buttonStyle}>
            Save List
          </button>
          <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
            {items.map((item, index) => (
              <li key={index}>
                {item}
                <button onClick={() => removeItem(index)} style={buttonStyle}>
                  Remove
                </button>
              </li>
            ))}
          </ul>
          {savedLists.length > 0 && (
            <div>
              <h3>Saved Lists:</h3>
              <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
                {savedLists.map((list, index) => (
                  <li key={index}>{list.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CreateShoppingList;
