import React, { useState } from "react";
import { Link } from "react-router-dom";

const SavedLists = ({ savedLists }) => {
  const buttonStyle = {
    textDecoration: "none",
    color: "#fff",
    backgroundColor: "#007bff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    margin: "10px",
    display: "inline-block",
  };

  return (
    <div>
      <h3>Saved Lists:</h3>
      <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
        {savedLists.map((list, index) => (
          <li key={index}>{list.name}</li>
        ))}
      </ul>
    </div>
  );
};

const CreateShoppingList = () => {
  const [listName, setListName] = useState("");
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [isListNamed, setIsListNamed] = useState(false);
  const [showSavedLists, setShowSavedLists] = useState(false);
  const [savedLists, setSavedLists] = useState([]);

  const handleListNameChange = (event) => {
    setListName(event.target.value);
  };

  const handleListNameSubmit = () => {
    if (listName.trim() !== "") {
      setIsListNamed(true);
    }
  };

  const handleInputChange = (event) => {
    setNewItem(event.target.value);
  };

  const addItem = () => {
    if (newItem.trim() !== "") {
      setItems([...items, newItem]);
      setNewItem("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };

  const clearItems = () => {
    setItems([]);
  };

  const saveList = () => {
    if (isListNamed && items.length > 0) {
      const shoppingList = {
        name: listName,
        items: [...items],
      };
      setSavedLists([...savedLists, shoppingList]);
      setIsListNamed(false);
      setListName("");
      setItems([]);
    }
  };

  const toggleSavedLists = () => {
    setShowSavedLists(!showSavedLists);
  };

  const buttonStyle = {
    textDecoration: "none",
    color: "#fff",
    backgroundColor: "#007bff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    margin: "10px",
    display: "inline-block",
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <Link to="/" style={buttonStyle}>
        Home
      </Link>
      <h1>Create Shopping List</h1>
      <button onClick={toggleSavedLists} style={buttonStyle}>
        {showSavedLists ? "Hide Saved Lists" : "Show Saved Lists"}
      </button>
      {showSavedLists && <SavedLists savedLists={savedLists} />}
      {/* ... (rest of your code remains unchanged) */}
    </div>
  );
};

export default CreateShoppingList;
