import React, { useState } from "react";
import { Link } from "react-router-dom";

const inputStyle = {
  width: "100%",
  padding: "8px",
  marginBottom: "10px",
};

const buttonStyle = {
  padding: "8px 12px",
  backgroundColor: "blue",
  color: "white",
  border: "none",
  cursor: "pointer",
};

const listStyle = {
  listStyleType: "none",
  padding: "0",
};

const linkStyle = {
  display: "block",
  fontSize: "16px",
  color: "blue",
  textDecoration: "none",
  marginTop: "10px",
};

const containerStyle = {
  textAlign: "center",
  fontFamily: "Arial, sans-serif",
};

const ShoppingList = () => {
  const [lists, setLists] = useState([]);
  const [newListName, setNewListName] = useState("");

  const createList = () => {
    if (newListName.trim() !== "") {
      const newList = { name: newListName, items: [] };
      setLists([...lists, newList]);
      setNewListName("");
    }
  };

  const handleInputChange = (e) => {
    setNewListName(e.target.value);
  };

  return (
    <div style={containerStyle}>
      <h1>Shopping Lists</h1>
      <input
        type="text"
        value={newListName}
        onChange={handleInputChange}
        placeholder="Enter list name"
        style={inputStyle}
      />
      <button onClick={createList} style={buttonStyle}>
        Create List
      </button>
      <ul style={listStyle}>
        {lists.map((list, index) => (
          <li key={index}>
            <Link to={`/shopping-list/${index}`} style={linkStyle}>
              {list.name}
            </Link>
          </li>
        ))}
      </ul>
      <Link to="/" style={linkStyle}>
        Go to Homepage
      </Link>
    </div>
  );
};

export default ShoppingList;
