import React, { Component } from "react";

class ShoppingListApp extends Component {
  constructor() {
    super();
    this.state = {
      items: [],
      newItem: "",
      collaborationURL: "",
      savedLists: [],
      selectedListIndex: -1,
      darkMode: false,
      language: "en", // Default language
    };
  }

  handleInputChange = (event) => {
    this.setState({ newItem: event.target.value });
  };

  addItem = () => {
    const { newItem, items } = this.state;
    if (newItem.trim() === "") return;

    this.setState({
      items: [...items, newItem],
      newItem: "",
    });
  };

  removeItem = (index) => {
    const { items } = this.state;
    const updatedItems = [...items];
    updatedItems.splice(index, 1);
    this.setState({ items: updatedItems });
  };

  inviteUsers = () => {
    this.setState({ collaborationURL: "Blank" });
  };

  saveList = () => {
    const { items, savedLists } = this.state;
    if (items.length === 0) return;

    const newList = {
      name: `List ${savedLists.length + 1}`,
      items: [...items],
    };

    this.setState({
      savedLists: [...savedLists, newList],
      items: [],
    });
  };

  deleteList = () => {
    const { selectedListIndex, savedLists } = this.state;
    if (selectedListIndex === -1) return;

    const updatedLists = [...savedLists];
    updatedLists.splice(selectedListIndex, 1);
    this.setState({ savedLists: updatedLists, selectedListIndex: -1 });
  };

  toggleDarkMode = () => {
    this.setState((prevState) => ({
      darkMode: !prevState.darkMode,
    }));
  };

  toggleLanguage = () => {
    this.setState((prevState) => ({
      language: prevState.language === "en" ? "cz" : "en",
    }));
  };

  render() {
    const {
      items,
      newItem,
      collaborationURL,
      savedLists,
      selectedListIndex,
      darkMode,
      language,
    } = this.state;

    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          minHeight: "100vh",
          backgroundColor: darkMode ? "#000000" : "#ffffff",
          color: darkMode ? "#ffffff" : "#000000",
          position: "relative",
        }}
      >
        <button
          onClick={this.toggleDarkMode}
          style={{
            position: "absolute",
            top: "20px",
            right: "20px",
            fontSize: "18px",
            padding: "10px 20px",
            backgroundColor: darkMode ? "#ffffff" : "#000000",
            color: darkMode ? "#000000" : "#ffffff",
          }}
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>

        <button
          onClick={this.toggleLanguage}
          style={{
            position: "absolute",
            top: "20px",
            left: "20px",
            fontSize: "18px",
            padding: "10px 20px",
            backgroundColor: darkMode ? "#ffffff" : "#000000",
            color: darkMode ? "#000000" : "#ffffff",
          }}
        >
          {language === "en" ? "Čeština" : "English"}
        </button>

        <h1 style={{ fontSize: "32px" }}>My Shopping List</h1>
        <input
          type="text"
          value={newItem}
          onChange={this.handleInputChange}
          style={{ fontSize: "18px", padding: "10px", width: "100%" }}
          placeholder={
            language === "en" ? "Add a new item" : "Přidat novou položku"
          }
        />
        <button
          onClick={this.addItem}
          style={{ fontSize: "18px", padding: "15px 30px" }}
        >
          {language === "en" ? "Add" : "Přidat"}
        </button>
        <button
          onClick={this.inviteUsers}
          style={{ fontSize: "18px", padding: "15px 30px" }}
        >
          {language === "en" ? "Invite Users" : "Pozvat uživatele"}
        </button>
        <button
          onClick={this.saveList}
          style={{ fontSize: "18px", padding: "15px 30px" }}
        >
          {language === "en" ? "Save List" : "Uložit seznam"}
        </button>
        <div>
          <p>{language === "en" ? "Collaboration URL:" : "URL spolupráce:"}</p>
          <p>{collaborationURL}</p>
        </div>
        <ul>
          {items.map((item, index) => (
            <li key={index}>
              {item}
              <button onClick={() => this.removeItem(index)}>
                {language === "en" ? "Remove" : "Odebrat"}
              </button>
            </li>
          ))}
        </ul>
        <div>
          <h2>{language === "en" ? "Saved Lists" : "Uložené seznamy"}</h2>
          <ul>
            {savedLists.map((list, index) => (
              <li key={index}>
                <strong>{list.name}</strong>
                <button
                  onClick={() => this.setState({ selectedListIndex: index })}
                >
                  {language === "en" ? "Select" : "Vybrat"}
                </button>
              </li>
            ))}
          </ul>
          {selectedListIndex !== -1 && (
            <div>
              <h3>{language === "en" ? "Selected List" : "Vybraný seznam"}</h3>
              <ul>
                {savedLists[selectedListIndex].items.map((item, idx) => (
                  <li key={idx}>{item}</li>
                ))}
              </ul>
              <button onClick={this.deleteList}>
                {language === "en"
                  ? "Delete Selected List"
                  : "Smazat vybraný seznam"}
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default ShoppingListApp;
