import React, { useState, useEffect } from "react";

const ShoppingList = () => {
  const defaultListName = "My Shopping List";
  const [listName, setListName] = useState(defaultListName);
  const [items, setItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [member, setMember] = useState(true); // For demo, assuming user is a member

  useEffect(() => {
    const savedListName = sessionStorage.getItem("shoppingListName");
    const savedItems = JSON.parse(sessionStorage.getItem("shoppingListItems"));

    if (savedListName) {
      setListName(savedListName);
    }
    if (savedItems) {
      setItems(savedItems);
    }
  }, []);

  const saveList = () => {
    sessionStorage.setItem("shoppingListName", listName);
    sessionStorage.setItem("shoppingListItems", JSON.stringify(items));
  };

  const addItem = () => {
    if (inputValue.trim() !== "") {
      setItems([...items, { name: inputValue, resolved: false }]);
      setInputValue("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = [...items];
    updatedItems.splice(index, 1);
    setItems(updatedItems);
  };

  const toggleResolved = (index) => {
    const updatedItems = [...items];
    updatedItems[index].resolved = !updatedItems[index].resolved;
    setItems(updatedItems);
  };

  const handleListNameChange = (e) => {
    if (member) {
      setListName(e.target.value);
    }
  };

  useEffect(() => {
    saveList();
  }, [listName, items]);

  const leaveList = () => {
    // Logic to leave the list (remove user from members)
  };

  const addMember = () => {
    // Logic to add member (only available to the owner)
  };

  return (
    <div>
      <h2>{defaultListName}</h2>
      {member && (
        <input
          type="text"
          value={listName}
          onChange={handleListNameChange}
          placeholder="Enter list name..."
        />
      )}
      {member && (
        <div>
          <button onClick={addMember}>Add Member</button>
          <button onClick={leaveList}>Leave List</button>
        </div>
      )}
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Add an item..."
      />
      <button onClick={addItem}>Add</button>
      <ul>
        {items.map((item, index) => (
          <li
            key={index}
            style={{ textDecoration: item.resolved ? "line-through" : "none" }}
          >
            {item.name}
            {!item.resolved && (
              <button onClick={() => toggleResolved(index)}>
                Mark as Resolved
              </button>
            )}
            <button onClick={() => removeItem(index)}>Remove</button>
          </li>
        ))}
      </ul>
      {/* Add filter options here */}
    </div>
  );
};

export default ShoppingList;
