import React, { useState } from "react";
import { Link } from "react-router-dom";

const CreateShoppingList = () => {
  const [listName, setListName] = useState("");
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [isListNamed, setIsListNamed] = useState(false);
  const [isOwner, setIsOwner] = useState(false);
  const [members, setMembers] = useState([]);
  const [resolvedItemsVisible, setResolvedItemsVisible] = useState(true);
  const [showOnlyUnresolved, setShowOnlyUnresolved] = useState(false);

  const handleListNameChange = (event) => {
    setListName(event.target.value);
  };

  const handleListNameSubmit = () => {
    if (listName.trim() !== "") {
      setIsListNamed(true);
      setIsOwner(true);
    }
  };

  const handleInputChange = (event) => {
    setNewItem(event.target.value);
  };

  const addItem = () => {
    if (newItem.trim() !== "") {
      setItems([...items, { name: newItem, resolved: false }]);
      setNewItem("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = [...items];
    updatedItems.splice(index, 1);
    setItems(updatedItems);
  };

  const toggleResolved = (index) => {
    const updatedItems = [...items];
    updatedItems[index].resolved = !updatedItems[index].resolved;
    setItems(updatedItems);
  };

  const addMember = (memberName) => {
    if (memberName.trim() !== "") {
      setMembers([...members, memberName]);
    }
  };

  const removeMember = (index) => {
    const updatedMembers = [...members];
    updatedMembers.splice(index, 1);
    setMembers(updatedMembers);
  };

  const leaveList = () => {
    setIsOwner(false);
  };

  const filteredItems = showOnlyUnresolved
    ? items.filter((item) => !item.resolved)
    : items;

  const buttonStyle = {
    textDecoration: "none",
    color: "#fff",
    backgroundColor: "#007bff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    margin: "10px",
    display: "inline-block",
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <Link to="/" style={buttonStyle}>
        Home
      </Link>
      <h1>Shopping List</h1>
      {!isListNamed ? (
        <div>
          <label>
            Enter List Name:
            <input
              type="text"
              value={listName}
              onChange={handleListNameChange}
              placeholder="Enter list name..."
              style={{ padding: "8px", margin: "10px" }}
            />
          </label>
          <br />
          <button onClick={handleListNameSubmit} style={buttonStyle}>
            Confirm List Name
          </button>
        </div>
      ) : (
        <div>
          <h2>List: {listName}</h2>
          {isOwner && (
            <div>
              <h3>Members:</h3>
              <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
                {members.map((member, index) => (
                  <li key={index}>
                    {member}
                    <button
                      onClick={() => removeMember(index)}
                      style={buttonStyle}
                    >
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
              <input
                type="text"
                value={newItem}
                onChange={handleInputChange}
                placeholder="Add a member..."
                style={{ padding: "8px", margin: "10px" }}
              />
              <button onClick={() => addMember(newItem)} style={buttonStyle}>
                Add Member
              </button>
              <button onClick={leaveList} style={buttonStyle}>
                Leave List
              </button>
            </div>
          )}
          <h3>Items:</h3>
          <label>
            Show Only Unresolved:
            <input
              type="checkbox"
              checked={showOnlyUnresolved}
              onChange={() => setShowOnlyUnresolved(!showOnlyUnresolved)}
              style={{ margin: "10px" }}
            />
          </label>
          <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
            {filteredItems.map((item, index) => (
              <li key={index}>
                <span
                  style={{
                    textDecoration: item.resolved ? "line-through" : "none",
                  }}
                >
                  {item.name}
                </span>
                {isOwner && (
                  <button
                    onClick={() => toggleResolved(index)}
                    style={buttonStyle}
                  >
                    {item.resolved ? "Unmark" : "Mark"} as Resolved
                  </button>
                )}
                {isOwner && (
                  <button onClick={() => removeItem(index)} style={buttonStyle}>
                    Remove
                  </button>
                )}
              </li>
            ))}
          </ul>
          {isOwner && (
            <div>
              <input
                type="text"
                value={newItem}
                onChange={handleInputChange}
                placeholder="Add an item..."
                style={{ padding: "8px", margin: "10px" }}
              />
              <button onClick={addItem} style={buttonStyle}>
                Add Item
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CreateShoppingList;
