import React, { useState } from "react";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import AddListModal from "./AddListModal";

const inputStyle = {
  width: "100%",
  padding: "8px",
  marginBottom: "10px",
};

const buttonStyle = {
  padding: "8px 12px",
  backgroundColor: "blue",
  color: "white",
  border: "none",
  cursor: "pointer",
};

const listStyle = {
  listStyleType: "none",
  padding: "0",
};

const linkStyle = {
  display: "block",
  fontSize: "16px",
  color: "blue",
  textDecoration: "none",
  marginTop: "10px",
};

const deleteButtonStyle = {
  marginLeft: "10px",
  padding: "5px 8px",
  backgroundColor: "red",
  color: "white",
  border: "none",
  cursor: "pointer",
};

const containerStyle = {
  textAlign: "center",
  fontFamily: "Arial, sans-serif",
};

const ShoppingList = () => {
  const [lists, setLists] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleAddList = (newListName) => {
    const newList = { name: newListName, items: [] };
    setLists([...lists, newList]);
    setIsModalOpen(false);
  };

  const removeList = (indexToRemove) => {
    const updatedLists = lists.filter((list, index) => index !== indexToRemove);
    setLists(updatedLists);
  };

  return (
    <div style={containerStyle}>
      <h1>Shopping Lists</h1>
      <button onClick={openModal} style={buttonStyle}>
        Add New List
      </button>
      <ul style={listStyle}>
        {lists.map((list, index) => (
          <li key={index}>
            <Link to={`/shopping-list/${index}`} style={linkStyle}>
              {list.name}
            </Link>
            <button onClick={() => removeList(index)} style={deleteButtonStyle}>
              Delete
            </button>
          </li>
        ))}
      </ul>
      <Link to="/" style={linkStyle}>
        Go to Homepage
      </Link>
      <AddListModal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        onAddList={handleAddList}
      />
    </div>
  );
};

export default ShoppingList;
