import React from "react";

const LanguageToggle = ({ toggleLanguage, language }) => {
  return (
    <button
      onClick={toggleLanguage}
      style={{
        position: "absolute",
        top: "20px",
        left: "20px",
        fontSize: "18px",
        padding: "10px 20px",
        backgroundColor: "#ffffff",
        color: "#000000",
      }}
    >
      {language === "en" ? "Switch to Czech" : "Přepnout do angličtiny"}
    </button>
  );
};

export default LanguageToggle;
