import React, { useState, useEffect } from "react";

const ShoppingList = () => {
  const [listName, setListName] = useState("");
  const [items, setItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [savedLists, setSavedLists] = useState([]);

  useEffect(() => {
    const savedShoppingLists =
      JSON.parse(localStorage.getItem("savedShoppingLists")) || [];
    setSavedLists(savedShoppingLists);
  }, []);

  useEffect(() => {
    localStorage.setItem("savedShoppingLists", JSON.stringify(savedLists));
  }, [savedLists]);

  const handleListNameChange = (e) => {
    setListName(e.target.value);
  };

  const addItem = () => {
    if (inputValue.trim() !== "") {
      setItems([...items, { name: inputValue, resolved: false }]);
      setInputValue("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = [...items];
    updatedItems.splice(index, 1);
    setItems(updatedItems);
  };

  const toggleResolved = (index) => {
    const updatedItems = [...items];
    updatedItems[index].resolved = !updatedItems[index].resolved;
    setItems(updatedItems);
  };

  const saveList = () => {
    if (listName.trim() !== "" && items.length > 0) {
      const newList = {
        name: listName,
        items: items,
      };
      setSavedLists([...savedLists, newList]);
      setListName("");
      setItems([]);
    }
  };

  const viewSavedList = (index) => {
    const selectedList = savedLists[index];
    setListName(selectedList.name);
    setItems(selectedList.items);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={listName}
          onChange={handleListNameChange}
          placeholder="Enter list name..."
        />
      </div>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Add an item..."
      />
      <button onClick={addItem}>Add</button>
      <button onClick={saveList}>Save List</button>
      <ul>
        {items.map((item, index) => (
          <li
            key={index}
            style={{ textDecoration: item.resolved ? "line-through" : "none" }}
          >
            {item.name}
            {!item.resolved && (
              <button onClick={() => toggleResolved(index)}>
                Mark as Resolved
              </button>
            )}
            <button onClick={() => removeItem(index)}>Remove</button>
          </li>
        ))}
      </ul>
      <div>
        <h3>Saved Lists</h3>
        <ul>
          {savedLists.map((savedList, index) => (
            <li key={index} onClick={() => viewSavedList(index)}>
              {savedList.name}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ShoppingList;
