// App.js
import React, { useState } from "react";
import "./App.css";
import ListTitle from "./components/ListTitle";
import MemberManagement from "./components/MemberManagement";
import ShoppingList from "./components/ShoppingList";

function App() {
  const [listTitle, setListTitle] = useState("My Shopping List");
  const [savedListTitle, setSavedListTitle] = useState(""); // State to store the saved title

  const handleSaveList = (title) => {
    setSavedListTitle(title);
  };

  return (
    <div className="App">
      <h1>{savedListTitle || "My Shopping List"}</h1>
      <ListTitle
        listTitle={listTitle}
        setListTitle={setListTitle}
        onSave={handleSaveList}
      />
      <div className="container">
        <MemberManagement />
        <ShoppingList />
      </div>
    </div>
  );
}

export default App;
