import React, { useState, useEffect } from "react";

const ShoppingList = () => {
  const defaultListName = "My Shopping List";
  const [listName, setListName] = useState(defaultListName);
  const [items, setItems] = useState([]);
  const [inputValue, setInputValue] = useState("");

  useEffect(() => {
    const savedListName = localStorage.getItem("shoppingListName");
    const savedItems = JSON.parse(localStorage.getItem("shoppingListItems"));

    if (savedListName) {
      setListName(savedListName);
    }
    if (savedItems) {
      setItems(savedItems);
    }
  }, []);

  const saveToLocalStorage = () => {
    localStorage.setItem("shoppingListName", listName);
    localStorage.setItem("shoppingListItems", JSON.stringify(items));
  };

  const addItem = () => {
    if (inputValue.trim() !== "") {
      setItems([...items, inputValue]);
      setInputValue("");
    }
  };

  const removeItem = (index) => {
    const updatedItems = [...items];
    updatedItems.splice(index, 1);
    setItems(updatedItems);
  };

  const handleListNameChange = (e) => {
    setListName(e.target.value);
  };

  useEffect(() => {
    saveToLocalStorage();
  }, [listName, items]);

  return (
    <div>
      <h2>{listName}</h2>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Add an item..."
      />
      <button onClick={addItem}>Add</button>
      <ul>
        {items.map((item, index) => (
          <li key={index}>
            {item}
            <button onClick={() => removeItem(index)}>Remove</button>
          </li>
        ))}
      </ul>
      <input
        type="text"
        value={listName}
        onChange={handleListNameChange}
        placeholder="Enter list name..."
      />
    </div>
  );
};

export default ShoppingList;
