import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  useLocation,
} from "react-router-dom";
import Home from "./components/Home";
import ShoppingList from "./components/ShoppingList";
import ItemList from "./components/ItemList";
import { ShoppingProvider } from "./components/ShoppingContext"; // Import your ShoppingContext
import { ThemeProvider } from "./ThemeContext"; // Import the ThemeProvider
import ThemeToggle from "./ThemeToggle"; // Import the ThemeToggle

function App() {
  const location = useLocation();
  const isHomepage = location.pathname === "/";

  return (
    <Router>
      <ShoppingProvider>
        <ThemeProvider>
          <div className="App">
            {isHomepage && <ThemeToggle />}{" "}
            {/* Render ThemeToggle only on the homepage */}
            <Switch>
              <Route exact path="/" component={Home} />
              <Route exact path="/shopping-list" component={ShoppingList} />
              <Route path="/shopping-list/:listId" component={ItemList} />
            </Switch>
          </div>
        </ThemeProvider>
      </ShoppingProvider>
    </Router>
  );
}

export default App;
