import React, { useState } from "react";
import { useHistory } from "react-router-dom";

function ShoppingList() {
  const [lists, setLists] = useState([]);
  const [newListName, setNewListName] = useState("");
  const history = useHistory();

  const createList = () => {
    if (newListName.trim() !== "") {
      const newList = { name: newListName, items: [] };
      setLists([...lists, newList]);
      setNewListName("");
      goToItemList(lists.length); // Go directly to the newly created list
    }
  };

  const handleInputChange = (e) => {
    setNewListName(e.target.value);
  };

  const goToItemList = (listIndex) => {
    history.push(`/shopping-list/${listIndex}`);
  };

  return (
    <div>
      <h1>Shopping Lists</h1>
      <input
        type="text"
        value={newListName}
        onChange={handleInputChange}
        placeholder="Enter list name"
      />
      <button onClick={createList}>Create List</button>
      <ul>
        {lists.map((list, index) => (
          <li key={index}>
            <button onClick={() => goToItemList(index)}>{list.name}</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShoppingList;
