import React, { useState } from "react";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import AddListModal from "./AddListModal";

const inputStyle = {
  width: "100%",
  padding: "8px",
  marginBottom: "10px",
};

const buttonStyle = {
  padding: "8px 12px",
  backgroundColor: "blue",
  color: "white",
  border: "none",
  cursor: "pointer",
  marginRight: "10px",
};

const listStyle = {
  listStyleType: "none",
  padding: "0",
};

const linkStyle = {
  display: "block",
  fontSize: "16px",
  color: "blue",
  textDecoration: "none",
  marginTop: "10px",
};

const deleteButtonStyle = {
  marginLeft: "10px",
  padding: "5px 8px",
  backgroundColor: "red",
  color: "white",
  border: "none",
  cursor: "pointer",
};

const containerStyle = {
  textAlign: "center",
  fontFamily: "Arial, sans-serif",
};

const ShoppingList = () => {
  const [lists, setLists] = useState([]);
  const [isAddListModalOpen, setIsAddListModalOpen] = useState(false);
  const [isDeleteConfirmationOpen, setIsDeleteConfirmationOpen] =
    useState(false);
  const [removeIndex, setRemoveIndex] = useState(null);

  const openAddListModal = () => {
    setIsAddListModalOpen(true);
  };

  const closeAddListModal = () => {
    setIsAddListModalOpen(false);
  };

  const openDeleteConfirmation = (index) => {
    setRemoveIndex(index);
    setIsDeleteConfirmationOpen(true);
  };

  const closeDeleteConfirmation = () => {
    setIsDeleteConfirmationOpen(false);
  };

  const handleAddList = (newListName) => {
    const newList = { name: newListName, items: [] };
    setLists([...lists, newList]);
    setIsAddListModalOpen(false);
  };

  const removeList = () => {
    if (removeIndex !== null) {
      const updatedLists = lists.filter((_, index) => index !== removeIndex);
      setLists(updatedLists);
      setRemoveIndex(null);
      closeDeleteConfirmation();
    }
  };

  return (
    <div style={containerStyle}>
      <h1>Shopping Lists</h1>
      <button onClick={openAddListModal} style={buttonStyle}>
        Add New List
      </button>
      <ul style={listStyle}>
        {lists.map((list, index) => (
          <li key={index}>
            <Link to={`/shopping-list/${index}`} style={linkStyle}>
              {list.name}
            </Link>
            <button
              onClick={() => openDeleteConfirmation(index)}
              style={deleteButtonStyle}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      <Link to="/" style={linkStyle}>
        Go to Homepage
      </Link>
      <Modal
        isOpen={isDeleteConfirmationOpen}
        onRequestClose={closeDeleteConfirmation}
        contentLabel="Confirm Delete Modal"
      >
        <div style={containerStyle}>
          <h2>Confirm Delete?</h2>
          <button onClick={removeList} style={buttonStyle}>
            Yes
          </button>
          <button onClick={closeDeleteConfirmation} style={buttonStyle}>
            No
          </button>
        </div>
      </Modal>
      <AddListModal
        isOpen={isAddListModalOpen}
        onRequestClose={closeAddListModal}
        onAddList={handleAddList}
      />
    </div>
  );
};

export default ShoppingList;
